function zbench_page_menu_args( $args ) {
	$args['show_home'] = true;
	return $args;
}
add_filter( 'wp_page_menu_args', 'zbench_page_menu_args' );

// Custom Comments List.
function mytheme_comment($comment, $args, $depth) {
   $GLOBALS['comment'] = $comment;
?>
<li <?php comment_class(); ?> id="li-comment-<?php comment_ID() ?>">
	<div id="comment-<?php comment_ID(); ?>">
		<div class="comment-author vcard">
			<?php echo get_avatar($comment,$size='40',$default='<path_to_url>' ); ?>
			<cite class="fn"><?php comment_author_link() ?></cite>
			<span class="comment-meta commentmetadata"><a href="<?php echo htmlspecialchars( get_comment_link( $comment->comment_ID ) ) ?>"><?php printf(__('%1$s at %2$s'), get_comment_date(),  get_comment_time()) ?></a><?php edit_comment_link(__('(Edit)'),'  ','') ?></span>
		</div>
		<?php if ($comment->comment_approved == '0') : ?>
		<em class="approved"><?php _e('Your comment is awaiting moderation.') ?></em>
		<br />
		<?php endif; ?>
		<div class="comment-text">
			<?php comment_text() ?>
		</div>
		<div class="reply">
			<?php comment_reply_link(array_merge( $args, array('depth' => $depth, 'max_depth' => $args['max_depth']))) ?>
		</div>
	</div>
<?php }

/* zBench Options */
class zBenchOptions {
	function getOptions() {
		$options = get_option('zBench_options');
		if (!is_array($options)) {
			$options['rss_url'] = '';
			$options['twitter_url'] = '';
			$options['facebook_url'] = '';			
			update_option('zBench_options', $options);
		}
		return $options;
	}
	function init() {
		if(isset($_POST['classic_save'])) {
			$options = zBenchOptions::getOptions();
			$options['rss_url'] = stripslashes($_POST['rss_url']);
			$options['twitter_url'] = stripslashes($_POST['twitter_url']);
			$options['facebook_url'] = stripslashes($_POST['facebook_url']);			

			update_option('zBench_options', $options);
		} else {
			zBenchOptions::getOptions();
		}
		add_theme_page("zBench Options", "zBench Options", 'edit_themes', basename(__FILE__), array('zBenchOptions', 'display'));
	}
	function display() {
		$options = zBenchOptions::getOptions();
?>
<form action="#" method="post" enctype="multipart/form-data" name="classic_form" id="classic_form">
